  const name = user?.name ?? "Anonymous";
  console.log(name);
}

// Method 4: Type guard (recommended)
function process(user?: User) {
  if (!user) {
    throw new Error("User required");
  }
  // TypeScript knows user is defined here
  console.log(user.name);
}

// Method 5: Early return
